            },
            pic_url: Icon_prefix + category_Icon[id] + "@Referer=",
            col_type: "icon_small_3"
        });
    } else {
        d.push({
            title: title,
            url: 'hiker://page/top',
            extra: {
                url: pd(data, 'a&&href') + '#noRecordHistory##noHistory#'
            },
            pic_url: Icon_prefix + category_Icon[id] + "@Referer=",
            col_type: "icon_small_3"
        });
    }
});
var Label_set_icon = ['136.png', '139.png', '141.png', '145.png', '140.png', '153.png', '142.png'];
let jdata = html.match(/window.__INITIAL_STATE__ =(.*?);</)[1];
//writeFile('/storage/emulated/0/Android/data/com.example.hikerview/files/Documents/rules/dzHouse/json/喜马拉雅一级缓存.json', jdata);
let pics = JSON.parse(jdata).store.ExplorePage;
let cate = [];
for (let k in pics.getRecommend) {
    cate = cate.concat(pics.getRecommend[k].albumList);
}
let recom = cate;
//log(cate);
let ulike = pics.guessYouLike;
//let 
//log(ulike);
cate = cate.concat(ulike);
//log(cate.length);
var fun = function(u, pics) {
    let id = u.match(/(\d+)/)[1];
    // log(u);
    let data = pics.filter(it => it.albumId == parseInt(id))[0];
    if (/http/.test(data.albumCoverPath)) {
        return data.albumCoverPath + '@Referer='
    } else {
        return 'https://imagev2.xmcdn.com/' + data.albumCoverPath + '@Referer='
    }
};
pdfa(html, "body&&.recommend-album-list").forEach(function(data, id) {
    let title = pdfh(data, 'h2.title&&Text');
    if (title == '猜你喜欢') {
        d.push({
            title: title + '\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t🔁换一批',
            pic_url: Icon_prefix + Label_set_icon[id] + "@Referer=",
            url: $().lazyRule(() => {
                refreshPage(false);
                return 'hiker://empty'
            }),
            col_type: "avatar"
        });
        var fdata = pdfa(data, "ul&&li");
        fdata = getRandomArrayValue(ulike, 6);
        fdata.forEach(function(zdata) {
            if (/http/.test(zdata.albumCoverPath)) {
                var img = zdata.albumCoverPath + '@Referer='
            } else {
                var img = 'http:' + zdata.albumCoverPath +
                    '@Referer='
            }
            if(/46543479/.test(zdata.albumUrl)) {
                d.push({
                title: zdata.albumTitle,
                url: $("此专辑有问题，网站不支持，是否跳转原网页查看").confirm(()=>{
                    return 'web://https://www.ximalaya.com/album/46543479'
                }),     
                pic_url: img,
                desc: zdata.intro
                });

            }else{
            d.push({
                title: zdata.albumTitle,
                url: 'https://www.ximalaya.com' + zdata.albumUrl + '#immersiveTheme#',     
                pic_url: img,
                desc: zdata.intro
            });
            }
        });
    } else {
        let title = pdfh(data, 'h2.title&&Text');
        d.push({
            title: title + '🍀更多',
            pic_url: Icon_prefix + Label_set_icon[id] + "@Referer=",
            url: 'hiker://page/fdetail',
            extra: {
                url: pd(data, '.head-rt&&a&&href') + "pfypage/#noRecordHistory##noHistory#[firstPage=" + pd(data, '.head-rt&&a&&href') + "#noRecordHistory##noHistory#" + "]"
            },
            col_type: "avatar"
        });
        var fdata = pdfa(data, "ul&&li");
        //fdata=recom.slice(0,12);
        fdata.forEach(function(zdata) {
            d.push({
                title: pdfh(zdata, 'a,1&&Text'),
                url: pd(zdata, 'a,1&&href') + '#immersiveTheme#',
                pic_url: fun(pd(zdata, 'a,1&&href'), cate),
                desc: pdfh(zdata, 'a,2&&Text')
            });
        });
    }
});

setResult(d);

