    let cfg = {
        一页章节数: 100,
        缓存个数: 15
    }

    var caches = [];

    function getCache() {
        let c = readFile("hiker://files/cache/xmly/chapter.json") || "[]";
        caches = JSON.parse(c)
        let url = MY_URL
        addListener("onRefresh", $.toString((url) => {
            let c = readFile("hiker://files/cache/xmly/chapter.json") || "[]";
            c = JSON.parse(c)
            let che = {
                url: url,
                page: 0,
                chapters: []
            };
            for (let i = 0; i < c.length; i++) {
                if (c[i].url == che.url) {
                    c[i] = che;
                    //log("clear:" + url)
                    saveFile("hiker://files/cache/xmly/chapter.json", JSON.stringify(c))
                    break
                }
            }
        }, url))
        for (let it of caches) {
            if (it.url == url) {
                return it;
            }
        }
        return {
            url: url,
            page: 0,
            chapters: []
        }
    }

    function saveCache(che) {
        //log(che.chapters.length)
        let c = caches
        if (c.length > cfg.缓存个数) {
            c.shift()
        }
        let exist = false
        for (let i = 0; i < c.length; i++) {
            if (c[i].url == che.url) {
                c[i] = che;
                exist = true;
                break
            }
        }
        if (!exist) {
            c.push(che)
        }
        saveFile("hiker://files/cache/xmly/chapter.json", JSON.stringify(c))
    }

    let cache = getCache();
    let page = pagenum;
    log("实际页数：" + page)
    log("缓存页数：" + cache.page)
    if (page == cache.page) {
        //已完结，目录全部缓存了
        return cache.chapters
    }
    //解析本地没有的缓存
    let urls = []
    let htmls = []
    //log(mp)
    for (let i = cache.page; i < page; i++) {
        let j = i + 1;
        var myurl = 'https://www.ximalaya.com/revision/album/v1/getTracksList?albumId=' + id + '&pageNum=' + j + '&sort=0&pageSize=100';
        urls.push({
            url: myurl,
            options: {
                headers: {
                    'User-Agent': PC_UA
                }
            }
        });
    }
    //log(urls)
    let d = [].concat(cache.chapters)
    htmls = htmls.concat(batchFetch(urls))
    //log(htmls)
    for (let it of htmls) {
        if (it == "") {
            break
        }
        let list = JSON.parse(it).data.tracks        
        //log(list.length)
        if (list.length == cfg.一页章节数) {
            //章节齐全的才放本地缓存
            cache.page = cache.page + 1
            cache.chapters = cache.chapters.concat(list)
        }
        d = d.concat(list)
    }
    saveCache(cache);
    return d;
}"},{"col_type":"movie_3","name":"公共方法","path":"xmly_PublicFunctiom","rule":"$.exports = {
