(function() {
    let html = fetch(MY_URL.replace("hiker://empty##", ""), {
        headers: {
            'content-type': 'text/plain; charst=gbk'
        }
    });
    const Config = $.require("hiker://page/Config.json?rule=本地资源管理");
    if (!html) {
        setResult([{
            col_type: "big_big_blank_block",
        }, {
            title: '<h1 style="text-align: center;">网络不佳，加载失败</h1>',
            col_type: "rich_text",
        }]);
        return;
    }
    let d = [];
    let title = pdfh(html, ".title&&Text");
    let desc = pdfa(html, ".bookcover&&p").map(v => pdfh(v, "p&&Text")).join("\n");
    d.push({
        title: title,
        pic_url: pd(html, ".pic&&img&&src"),
        desc: desc,
        url: $(desc).confirm(() => {}),
        col_type: "movie_1_vertical_pic_blur",
        extra: {
            gradient: true
        }
    });
    /*d.push({
        col_type: "big_blank_block"
    });
    d.push({
        col_type: "line_blank"
    });*/
    let path = "file://" + Config.externalImportPath + title + ".txt";
    let butText = "下载到阅读器";
    let butCol = "text_center_1";

    if (fileExist(path)) {
        butText = "重新下载";
        butCol = "text_3";
        d.push({
            title: "打开书架",
            url: "hiker://page/externalimportTxt.view?rule=本地资源管理",
            col_type: butCol
        });
        d.push({
            title: "本地阅读",
            url: "hiker://page/txtParser.view?rule=本地资源管理",
            col_type: butCol,
            extra: {
                isCache: true,
                path: path
            }
        });
    }
    d.push({
        title: butText,
        url: !Config.externalImportPath ? "toast://未安装本地资源管理" : $(pd(html, ".greenBtn:matches(下载)&&a&&href") + "#noLoading#").lazyRule((title, path) => {
            showLoading("下载中...");
            input = pd(fetch(input), ".greenBtn&&href");
            downloadFile(input, path);
            refreshPage(false);
            log("『" + title + "』下载的完成");
            hideLoading();
            toast("『" + title + "』下载的完成");
            return "hiker://page/txtParser.view?rule=本地资源管理&path=" + encodeURIComponent(path);
        }, title, path),
        col_type: butCol,
        extra: {
            isCache: true
        }
    });
    d.push({
        title: "<big><strong>小说简介</strong></big><br>　　" + pdfh(html, ".bookintro&&.con&&Text"),
        col_type: "rich_text",
    });
    d.push({
        title: '““””<small><font color=#20b2aa>数据来源于对应的网站，软件和规则不提供任何内容！</font></small>',
        desc: '““””<small><font color=#ff7f50>本规则仅限学习与交流，请导入后24小时内删除！</font></small>',
        url: MY_URL,
        col_type: 'text_center_1'
    });
    setResult(d);
})()

