var lazy=`@lazyRule=js:var code=parseDomForHtml(fetch(input),"#jp-lines&&li&&data-code").split("").reverse();var m="";var url=base64Decode(code.join(""));url+'#isMusic=true#'`


//选集名称
for (var i in list) {

    var title = parseDomForHtml(list[i], 'a&&Text').replace(name,"").replace(/《|》/g,"");

